CREATE TABLE CART_ITEMS (ID INTEGER DEFAULT AUTOINCREMENT: start 1 increment 1  NOT NULL GENERATED ALWAYS AS IDENTITY, USER_ID INTEGER NOT NULL, PRODUCT_ID INTEGER NOT NULL, QUANTITY INTEGER DEFAULT 1 , ADDED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (ID));

CREATE TABLE PRODUCTS (ID INTEGER DEFAULT AUTOINCREMENT: start 1 increment 1  NOT NULL GENERATED ALWAYS AS IDENTITY, "NAME" VARCHAR(200) NOT NULL, PRICE DECIMAL(10, 2) NOT NULL, CATEGORY VARCHAR(50) NOT NULL, DESCRIPTION CLOB(2147483647), SELLER_ID INTEGER NOT NULL, IMAGE_URL VARCHAR(500), LOCATION VARCHAR(100), PAYMENT_OPTION VARCHAR(100), PRODUCT_CONDITION VARCHAR(20) DEFAULT 'used' , QUANTITY INTEGER DEFAULT 1 , IS_AVAILABLE SMALLINT DEFAULT 1 , CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (ID));

CREATE TABLE USERS (ID INTEGER DEFAULT AUTOINCREMENT: start 1 increment 1  NOT NULL GENERATED ALWAYS AS IDENTITY, USERNAME VARCHAR(50) NOT NULL, PASSWORD VARCHAR(255) NOT NULL, EMAIL VARCHAR(100) NOT NULL, PHONE_NUMBER VARCHAR(20), USER_TYPE VARCHAR(10) DEFAULT 'buyer' , IS_ACTIVE SMALLINT DEFAULT 1 , CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (ID));
