CREATE TABLE USERS (ID INTEGER DEFAULT AUTOINCREMENT: start 1 increment 1  NOT NULL GENERATED ALWAYS AS IDENTITY, FIRSTNAME VARCHAR(50) NOT NULL, LASTNAME VARCHAR(50) NOT NULL, EMAIL VARCHAR(100) NOT NULL, USERNAME VARCHAR(50) NOT NULL, PASSWORD VARCHAR(255) NOT NULL, PHONENUMBER VARCHAR(20), MAJOR VARCHAR(100), USERTYPE VARCHAR(20) NOT NULL, STATUS VARCHAR(20), CREATEDAT TIMESTAMP DEFAULT CURRENT_TIMESTAMP , ISACTIVE BOOLEAN DEFAULT true , PRIMARY KEY (ID));
